INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '201703006500300', 
    '201703006100000', 
    '2', 
    'BAB II - LAYANAN IPTV', 
    '3', 
    'Dalam penyelenggaraan layanan IPTV, Penyelenggara wajib:
