INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202103005502800', 
    '202103005100000', 
    '7', 
    'BAB VII - KERJA SAMA DAN FASILITASI INFRASTRUKTUR TELEKOMUNIKASI', 
    '28', 
    '(1) Tarif pemanfaatan infrastruktur pasif merupakan sejumlah biaya yang dibebankan penyedia infrastruktur pasif kepada Penyelenggara Telekomunikasi sesuai dengan kerja sama yang disepakati.
