);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202103005509500', 
    '202103005100000', 
    '10', 
    'BAB X - INTERKONEKSI', 
    '95', 
    '(1) Layanan Originasi sebagaimana dimaksud dalam Pasal 94 huruf a terdiri atas:
	a. Originasi Lokal;
	b. Originasi Jarak Jauh; dan
	c. Originasi Internasional.
