INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202103005518000', 
    '202103005100000', 
    '14', 
    'BAB XIV - PENGIRIMAN LAYANAN PESAN PENDEK (SHORT MESSAGE SERVICE/SMS) PENAWARAN MARKETING OLEH PENYELENGGARA JARINGAN BERGERAK SELULER DAN/ATAU PENYELENGGARA JASA PENYEDIAAN KONTEN MELALUI JARINGAN BERGERAK SELULER', 
