INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202103005518700', 
    '202103005100000', 
    '15', 
    'BAB XV - PETUNJUK PELAKSANAAN TARIF ATAS PENERIMAAN NEGARA BUKAN PAJAK DARI PUNGUTAN BIAYA HAK PENYELENGGARAAN TELEKOMUNIKASI DAN KONTRIBUSI KEWAJIBAN PELAYANAN UNIVERSAL/UNIVERSAL SERVICE OBLIGATION', 
    '187', 
    '(1) Setiap Penyelenggara Jaringan Telekomunikasi dan/atau Penyelenggara Jasa Telekomunikasi wajib membayar BHP Telekomunikasi.
(2) Setiap Penyelenggara Jaringan Telekomunikasi dan/atau Penyelenggara Jasa Telekomunikasi wajib memberikan kontribusi KPU/USO dalam bentuk dana berdasarkan persentase tertentu dari Pendapatan Kotor Penyelenggaraan Telekomunikasi dan/atau kontribusi lainnya.
(3) Dalam hal bentuk kontribusi lainnya sebagaimana dimaksud pada ayat (2) belum ditetapkan oleh Menteri, Penyelenggara Jaringan Telekomunikasi dan/atau Penyelenggara Jasa Telekomunikasi wajib memberikan Kontribusi KPU/USO dalam bentuk dana berdasarkan persentase tertentu dari Pendapatan Kotor Penyelenggaraan Telekomunikasi.', 
    'Effective'
);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202103005518800', 
    '202103005100000', 
    '15', 
    'BAB XV - PETUNJUK PELAKSANAAN TARIF ATAS PENERIMAAN NEGARA BUKAN PAJAK DARI PUNGUTAN BIAYA HAK PENYELENGGARAAN TELEKOMUNIKASI DAN KONTRIBUSI KEWAJIBAN PELAYANAN UNIVERSAL/UNIVERSAL SERVICE OBLIGATION', 
    '188', 
    '(1) Besaran BHP Telekomunikasi dipungut sebesar 0,50% (nol koma lima puluh persen) dari Pendapatan Kotor Penyelenggaraan Telekomunikasi sesuai dengan ketentuan peraturan perundang-undangan.
(2) Besaran Kontribusi KPU/USO dipungut sebesar 1,25% (satu koma dua puluh lima persen) dari Pendapatan Kotor Penyelenggaraan Telekomunikasi sesuai dengan ketentuan peraturan perundang- undangan.', 
    'Effective'
);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
