INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202403004508200', 
    '202403004100000', 
    '4', 
    'BAB IV - FASILITASI PENYELENGGARAAN KOORDINASI', 
    '82', 
    '(1) Dinas pada tingkat kabupaten/kota memfasilitasi penyelenggaraan koordinasi bidang komunikasi dan informatika dengan Perangkat Daerah lain, Pemerintah Provinsi di Wilayahnya, Dinas Provinsi dan/atau kabupaten/kota lain, Instansi Pusat, dunia usaha, lembaga luar negeri, dan lembaga resmi lainnya sesuai dengan ketentuan peraturan perundang-undangan.
(2) Dinas pada tingkat Provinsi memfasilitasi penyelenggaraan koordinasi bidang komunikasi dan informatika dengan Perangkat Daerah lain, Dinas kabupaten/kota di wilayahnya, Dinas Provinsi dan/atau kabupaten/kota lain, Instansi Pusat, dunia usaha, lembaga luar negeri, dan lembaga resmi lainnya sesuai dengan ketentuan peraturan perundang-undangan.
(3) Fasilitasi penyelenggaraan koordinasi sebagaimana dimaksud pada ayat (1) dan ayat (2) dilaksanakan dalam rangka harmonisasi kegiatan dan penyelenggaraan pemerintahan yang baik.', 
    'Effective'
);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202403004508300', 
    '202403004100000', 
    '5', 
    'BAB V - MONITORING DAN EVALUASI', 
    '83', 
    '(1) Monitoring dan evaluasi Pemerintah Daerah bertujuan untuk mengukur kemajuan dan meningkatkan kualitas penyelenggaraan urusan konkuren bidang komunikasi dan informatika di lingkungan Pemerintah Daerah.
(2) Dinas melakukan monitoring dan evaluasi terhadap program dan kegiatan Dinas sesuai dengan ketentuan peraturan perundang-undangan.
(3) Dinas mengkoordinasikan dan melaksanakan monitoring dan evaluasi sebagaimana dimaksud pada ayat (1) terhadap Perangkat Daerah di lingkungan Pemerintah Daerahnya sesuai dengan ketentuan peraturan perundang-undangan.
(4) Monitoring dan evaluasi sebagaimana dimaksud pada ayat (2) dilaksanakan paling sedikit 1 (satu) kali dalam setahun.
(5) Hasil monitoring dan evaluasi di tingkat provinsi dilaporkan kepada Kepala Daerah dan ditembuskan kepada Menteri untuk dijadikan dasar pengambilan kebijakan bagi pembenahan penyelenggaraan urusan konkuren bidang komunikasi dan informatika.
(6) Hasil monitoring dan evaluasi di tingkat kabupaten/kota dilaporkan kepada Kepala Daerah dan ditembuskan kepada Menteri dan gubernur untuk dijadikan dasar pengambilan kebijakan bagi pembenahan penyelenggaraan urusan konkuren bidang komunikasi dan informatika.', 
    'Effective'
);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202403004508400', 
    '202403004100000', 
    '6', 
    'BAB VI - PEMBINAAN DAN PENGAWASAN', 
    '84', 
    '(1) Menteri melaksanakan pembinaan dan pengawasan bersifat teknis terhadap penyelenggaraan urusan konkuren bidang komunikasi dan informatika di lingkungan Pemerintah Daerah provinsi.
(2) Gubernur sebagai wakil Pemerintah Pusat melaksanakan pembinaan dan pengawasan teknis terhadap penyelenggaraan urusan konkuren bidang komunikasi dan informatika terhadap Pemerintah Daerah kabupaten/kota di wilayahnya.
(3) Dalam hal gubernur sebagai wakil Pemerintah Pusat belum mampu melakukan pembinaan dan pengawasan teknis terhadap penyelenggaraan urusan konkuren bidang komunikasi dan informatika sebagaimana dimaksud pada ayat (2), Menteri melaksanakan pembinaan dan pengawasan teknis terhadap penyelenggaraan urusan konkuren bidang komunikasi dan informatika kepada Pemerintah Daerah kabupaten/kota dengan berkoordinasi kepada gubernur sebagai wakil Pemerintah Pusat.
(4) Pembinaan dan pengawasan sebagaimana dimaksud pada ayat (1) dilakukan dengan memperhatikan ketaatan terhadap NSPK dan dampak terhadap pelaksanaan urusan bidang komunikasi dan informatika di daerah.
(5) Pembinaan dan pengawasan sebagaimana dimaksud dalam ayat (1) bersifat teknis terhadap penyelenggaraan urusan konkuren bidang komunikasi dan informatika Pemerintah Daerah provinsi, kabupaten, dan kota untuk memastikan terlaksananya program dan kegiatan urusan konkuren bidang kominfo dapat dilakukan secara maksimal.
(6) Pembinaan dan pengawasan sebagaimana dimaksud pada ayat (1) dapat dilakukan dengan memanfaatkan layanan berbasis TIK sesuai dengan ketentuan perundang-undangan.
(7) Dalam pelaksanaan pembinaan dan pengawasan sebagaimana dimaksud ayat (1) Menteri dapat berkoordinasi dengan menteri yang menyelenggarakan urusan pemerintahan dalam negeri sesuai dengan ketentuan perundang-undangan.', 
    'Effective'
);


INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202403004508500', 
    '202403004100000', 
    '6', 
    'BAB VI - PEMBINAAN DAN PENGAWASAN', 
    '85', 
