INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '202102046506400', 
    '202102046100000', 
    '4', 
    'BAB IV - PENGGUNAAN SPEKTRUM FREKUENSI RADIO', 
    '64', 
    '(1) Pengakhiran masa laku izin penggunaan Spektrum Frekuensi Radio atas dasar pencabutan sebagaimana dimaksud dalam Pasal 63 ayat (2) huruf a dilakukan karena:
