INSERT INTO articles (id, regulation_id, chapter_number, chapter_about, article_number, text, status) 
VALUES (
    '200801011501000', 
    '200801011100000', 
    '3', 
    'BAB III - INFORMASI DOKUMEN DAN TANDA TANGAN ELEKTRONIK', 
